package javaprogramdemo;

public class ThreeGreaterNO {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=9,b=5,c=2;
		if(a>b && a>c)
			System.out.println(a+"is that greater");
		else if(b>c)
			System.out.println(b+"is that greater");
		else 
			System.out.println(c+"is that greater");
	}

}
